mapper.insert:
insert into bis_store (id, store_name, store_address, 
  store_phone, store_pic, store_introduction, 
  store_longitude, store_latitude, store_user_id, 
  store_sort_num, store_creat_time, store_status
  )
values (#{id,jdbcType=BIGINT}, #{storeName,jdbcType=VARCHAR}, #{storeAddress,jdbcType=VARCHAR}, 
  #{storePhone,jdbcType=VARCHAR}, #{storePic,jdbcType=VARCHAR}, #{storeIntroduction,jdbcType=VARCHAR}, 
  #{storeLongitude,jdbcType=VARCHAR}, #{storeLatitude,jdbcType=VARCHAR}, #{storeUserId,jdbcType=BIGINT}, 
  #{storeSortNum,jdbcType=BIGINT}, #{storeCreatTime,jdbcType=TIMESTAMP}, #{storeStatus,jdbcType=INTEGER}
  )

mapper.select:
SELECT id, store_name storeName, store_address storeAddress, store_phone storePhone, store_pic storePic, store_introduction storeIntroduction, store_longitude storeLongitude, store_latitude storeLatitude, store_user_id storeUserId, store_sort_num storeSortNum, DATE_FORMAT(store_creat_time,'%Y-%m-%d %T') storeCreatTime, store_status storeStatus from bis_store
and store_user_id=#{userId}
and store_name=#{storeName}
and store_status=#{storeStatus}
